

//VARIÁVEIS
	//AQUÍ ESTÃO AS VARIÁVEIS DA FORMATAÇÃO DA BOX MODEL PARA BUSCA
	var bmBusca = document.getElementById("bm-busca");
	var	btnLupa = document.getElementById("btn-bm-lupa");
	var fecharBmBusca = document.getElementsByClassName("close-bm-busca")[0];
	//OPEN A FORMATAÇÃO DA BOX MODEL PARA BUSCA
	btnLupa.onclick = function() {
		bmBusca.style.display = "block";
	}

	fecharBmBusca.onclick = function() {
		bmBusca.style.display = "none";
	}

	window.onclick = function(event) {
	if (event.target == bmBusca) {
			bmBusca.style.display = "none";
		}
	}


